css:
section {
  padding: 1rem;
}

.w--block-container {
  background-color: transparent;
  padding: 2rem;
  border: 1px solid var(--pico-border-color, #ccc);
  border-radius: var(--pico-border-radius, 0.5rem);
  margin: 0 auto;
  --block-container-max-width: 700px;
}

.w--block-container h2 {
  margin-bottom: 1rem;
  font-size: 1.5rem;
}

form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

fieldset {
  display: grid;
  grid-template-columns: auto 120px;
  gap: 0.5rem;
}


input[type="email"],
input[type="submit"] {
  width: 100%;
  padding: 0.75rem;
  font-size: 1rem;
  border: 1px solid var(--pico-border-color, #ccc);
  border-radius: var(--pico-border-radius, 0.5rem);
}

input[type="submit"] {
  background-color: var(--pico-primary, #007bff);
  color: var(--pico-primary-inverse, #fff);
  cursor: pointer;
  border: none;
}

input[type="submit"]:hover {
  background-color: var(--pico-primary-hover, #0056b3);
}
